package mybiotools.gwascommons.associationresults

import mybiotools.gwascommons._
import org.scalatest.FunSpec
import org.scalatest.Matchers
import mybiotools.stringstore._
import java.io._

class AssociationResultsSpec extends FunSpec with Matchers {

  implicit def string2string8(s: String) = StringStore(s)

  describe("binary format") {
    it("write int") {
      def testint(i: Int) = {
        val baos = new ByteArrayOutputStream()
        val dos = new DataOutputStream(baos)
        FullAssociationResult.writeInt(i, dos)
        val bais = new ByteArrayInputStream(baos.toByteArray)
        val dis = new DataInputStream(bais)
        FullAssociationResult.readInt(dis) should equal(i)
      }
      testint(0)
      testint(-1)
      testint(1)
      testint(Byte.MaxValue)
      testint(Byte.MinValue)
      testint(Byte.MaxValue + 1)
      testint(Byte.MinValue + 1)
      testint(Byte.MaxValue - 1)
      testint(Byte.MinValue - 1)

      testint(Short.MaxValue)
      testint(Short.MinValue)
      testint(Short.MaxValue + 1)
      testint(Short.MinValue + 1)
      testint(Short.MaxValue - 1)
      testint(Short.MinValue - 1)

      testint(Int.MaxValue)
      testint(Int.MinValue + 1)
      testint(Int.MaxValue - 1)
    }
    it("simple") {
      val f = FullAssociationResult(
        name = StringStore("sdfs"),
        genomicLocation = GenomicLocation(3, 3),
        pValue = 0.1,
        test = s8"test",
        effectSize = 1.0,
        statistic = 1.0,
        error = Some(1.0),
        allele = SimpleAllele("DFS"),
        phenotypeName = s8"dfs",
        interaction = None,
        nonMiss = 300,
        exception = None
      )

      FullAssociationResult.fromBytes(f.toBytes) should equal(f)
    }
    it("iterator") {
      val f1 = FullAssociationResult(
        name = StringStore("sdfs"),
        genomicLocation = GenomicLocation(3, 3),
        pValue = 0.1,
        test = s8"test",
        effectSize = 1.0,
        statistic = 1.0,
        error = Some(1.0),
        allele = SimpleAllele("DFS"),
        phenotypeName = s8"dfs",
        interaction = None,
        nonMiss = 1000,
        exception = None
      )
      val f2 = FullAssociationResult(
        name = StringStore("sdfs"),
        genomicLocation = GenomicLocation(3, 3),
        pValue = 0.1,
        test = s8"test",
        effectSize = 1.0,
        statistic = 1.0,
        error = None,
        allele = SimpleAllele("DFS"),
        phenotypeName = s8"dfs",
        interaction = Some("dfs"),
        nonMiss = 3,
        exception = Some("dfsdf")
      )
      val f3 = FullAssociationResult(
        name = StringStore("sdfs"),
        genomicLocation = GenomicLocation(3, 3),
        pValue = 0.1,
        test = s8"",
        effectSize = 1.0,
        statistic = 1.0,
        error = None,
        allele = SimpleAllele("DFS"),
        phenotypeName = s8"dfs",
        interaction = Some("dfs"),
        nonMiss = 50000,
        exception = Some("dfsdf")
      )

      val list = List(f1, f2, f3)
      val ar: Array[Byte] = list.toArray.flatMap(f => FullAssociationResult.encodeSize(f.toBytes))

      val bio = new ByteArrayInputStream(ar)
      FullAssociationResult.fromInputStream(bio).toList should equal(list)
    }
  }

  describe("Read in a association result file") {

    it("should read a simple table") {
      val text = """SNP BP dsfa CHR fasdsf P
    |rs1 1 dfasf chr1 sadfsad NA
    |rs2 2 dfsdaf chr2 dfasdf 0.5""".stripMargin

      val source = scala.io.Source.fromString(text)
      val res = readAssociationResultsFromFileWithHeader(source, None, "P", "SNP", Some("CHR"), Some("BP")).toList

      val expected = List(
        AssociationResultImpl(name = "rs2", genomicLocation = GenomicLocation(2, "chr2"), pValue = 0.5)
      )
      res should equal(expected)
    }

  }
}